css: /* make icon relative to title container */
.notion-scroller[style*='display: flex; flex-direction: column']
  > [style$='z-index: 3; flex-shrink: 0;']
  > :first-child {
  padding-top: 32px;
  position: relative;
}
/* size icon */
.notion-scroller[style*='display: flex; flex-direction: column']
  > [style$='z-index: 3; flex-shrink: 0;']
  > :first-child
  > :first-child
  .notion-record-icon[style*='height: 140px'],
.notion-scroller[style*='display: flex; flex-direction: column']
  > [style$='z-index: 3; flex-shrink: 0;']
  > :first-child
  > :first-child
  .notion-record-icon[style*='height: 78px'] {
  width: 32px !important;
  height: 32px !important;
}
.notion-scroller[style*='display: flex; flex-direction: column']
  > [style$='z-index: 3; flex-shrink: 0;']
  > :first-child
  > :first-child
  .notion-record-icon[style*='height: 140px']
  *,
.notion-scroller[style*='display: flex; flex-direction: column']
  > [style$='z-index: 3; flex-shrink: 0;']
  > :first-child
  > :first-child
  .notion-record-icon[style*='height: 78px']
  * {
  width: 100% !important;
  height: 100% !important;
}
/* position icon */
.notion-scroller[style*='display: flex; flex-direction: column']
  > [style$='z-index: 3; flex-shrink: 0;']
  > :first-child
  > :first-child
  .notion-record-icon[style*='height: 140px'],
.notion-scroller[style*='display: flex; flex-direction: column']
  > [style$='z-index: 3; flex-shrink: 0;']
  > :first-child
  > :first-child
  .notion-record-icon[style*='height: 78px'] {
  margin-top: 16px !important;
  margin-right: 8px !important;
  float: left;
}
.notion-scroller[style*='display: flex; flex-direction: column']
  > [style$='z-index: 3; flex-shrink: 0;']
  > :first-child
  > :first-child
  .notion-page-controls {
  position: absolute !important;
  top: -5px;
}
/* emojis */
.notion-scroller[style*='display: flex; flex-direction: column']
  > [style$='z-index: 3; flex-shrink: 0;']
  > :first-child
  > :first-child
  .notion-record-icon
  [style*='font-size: 78px'] {
  font-size: 32px !important;
}
/* remove extra space at top when there's no icon or cover */
.notion-page-controls[style*='margin-top: 80px'],
.notion-page-controls[style*='margin-top: 32px'] {
  margin-top: 8px !important;
}

css: /* remove icon margin */
.notion-topbar .notranslate[style*='margin-right: 8px'] [role='button'] .notion-record-icon {
  margin-right: 0 !important;
}
/* position page title */
.notion-topbar .notranslate[style*='margin-right: 8px'] .notion-selectable:hover {
  z-index: 999;
}
.notion-topbar .notranslate[style*='margin-right: 8px'] [role='button'] {
  position: relative;
}
.notion-topbar
  .notranslate[style*='margin-right: 8px']
  [role='button']
  .notion-record-icon
  + div {
  background: var(--theme--ui_interactive-active);
  height: 24px;
  padding: 0 6px;
  border-radius: 3px;
  position: absolute;
  left: 26px;
  display: flex;
  align-items: center;
  pointer-events: none;
}
.notion-topbar
  .notranslate[style*='margin-right: 8px']
  [role='button']
  .notion-record-icon
  + div {
  transition: opacity 20ms ease-in, max-width 300ms ease-in !important;
}
/* slide out title on hover */
.notion-topbar
  .notranslate[style*='margin-right: 8px']
  [role='button']:not(:hover)
  .notion-record-icon
  + div {
  opacity: 0;
  max-width: 0px !important;
}
.notion-topbar
  .notranslate[style*='margin-right: 8px']
  [role='button']:hover
  .notion-record-icon
  + div {
  opacity: 1;
}
.notion-topbar
  .notranslate[style*='margin-right: 8px']
  [role='button']:hover
  .notion-record-icon,
.notion-topbar
  .notranslate[style*='margin-right: 8px']
  [role='button']:hover
  .notion-record-icon
  + div {
  transition-delay: 200ms !important;
}

